    AND TOPNAME= #{championName1}


    AND JUNGLENAME= #{championName2}


    AND MIDDLENAME= #{championName3}


    AND BOTTOMNAME= #{championName4}


    AND UTILITYNAME= #{championName5}

ORDER BY ${sort} DESC FETCH FIRST 15 ROWS ONLY
